html:
{# =========================================================
   ERPNext / Frappe Print Format (Jinja)
   Hourly Drilling Report — Drilling Shift Summary (A4 Landscape)

   ✅ Collects ALL rows (first hour → last hour)
   ✅ Drill table totals == DRILLED MATERIAL totals (same aggregation rows)
   ✅ Monthly Stats query SAFE (no unknown column errors)
   ✅ Forecasted Meters fetched from drilling_meters_forecast
   ✅ ALL numbers displayed with 0 decimals
   ========================================================= #}

<style>
  @media print { @page { size: A4 landscape; margin: 6mm; } }
  * { box-sizing: border-box; }
  body { font-family: Arial, Helvetica, sans-serif; font-size: 10px; color: #222; }

  .title { text-align: center; font-size: 16px; font-weight: 700; margin: 0 0 4px 0; color: #1F4E79; }
  .meta { text-align: center; margin: 0 0 6px 0; white-space: nowrap; font-size: 11px; }

  .pill{
    display:inline-block; padding: 1px 6px; border: 1px solid #888; border-radius: 4px;
    font-weight: bold; min-width: 18px; text-align:center; background:#fff2cc;
  }

  table.layout { width: 100%; border-collapse: collapse; }
  td.left { width: 74%; vertical-align: top; padding-right: 8px; }
  td.right { width: 26%; vertical-align: top; }

  .block { border: 1px solid #1f4e79; margin-bottom: 6px; }
  .block-title { background: #1f4e79; color: #fff; padding: 4px 6px; font-weight: 700; font-size: 11px; }

  table.tbl { width: 100%; border-collapse: collapse; }
  table.tbl th, table.tbl td { border: 1px solid #1f4e79; padding: 4px; text-align: center; vertical-align: middle; }
  table.tbl th { background: #d9e2f3; font-weight: 700; color: #000; }

  .right-align { text-align: right; }
  .stats-label { background: #d9e2f3; font-weight: 700; }
  .stats-value { background: #fff2cc; font-weight: 700; text-align: center; }

  input[type=number], input[type=text]{
    width: 100%; box-sizing: border-box; text-align: center; font-weight: bold;
    border: 1px solid #ccc; border-radius: 2px; padding: 2px 2px; font-size: 9px; background: #fff;
  }

  input.start-hours, input.end-hours{
    font-family: monospace; font-variant-numeric: tabular-nums;
    width: 52px; max-width: 52px; min-width: 52px;
  }
  input.total-hours{
    font-family: monospace; font-variant-numeric: tabular-nums;
    background: #fff2cc;
    width: 42px; max-width: 42px; min-width: 42px;
  }

  .hours-cell { padding: 3px; }
  .hours-cell.start, .hours-cell.end { width: 62px; }
  .hours-cell.total { width: 52px; }

  table.tbl td { line-height: 1.15; }
  .block, table { page-break-inside: avoid; }

  .debug { font-family: monospace; font-size: 9px; text-align:left; padding:6px; }
</style>

{# ---------- helpers ---------- #}
{% macro fmt1_or_dash(n) -%}
  {# ✅ ONLY CHANGE: 0 decimal places #}
  {{ "-" if n is none else "{:,.0f}".format(n|float) }}
{%- endmacro %}

{# Pick first non-empty value among up to 6 possible fieldnames #}
{% macro pick(r, a, b, c, d, e, f) -%}
  {# r is a child row (Document). r.get(...) works. #}
  {% set v = r.get(a) %}
  {% if v is not none and (v|string|trim) != "" %}{{ v }}{% else %}
    {% set v = r.get(b) %}
    {% if v is not none and (v|string|trim) != "" %}{{ v }}{% else %}
      {% set v = r.get(c) %}
      {% if v is not none and (v|string|trim) != "" %}{{ v }}{% else %}
        {% set v = r.get(d) %}
        {% if v is not none and (v|string|trim) != "" %}{{ v }}{% else %}
          {% set v = r.get(e) %}
          {% if v is not none and (v|string|trim) != "" %}{{ v }}{% else %}
            {% set v = r.get(f) %}
            {% if v is not none and (v|string|trim) != "" %}{{ v }}{% else %}{{ "" }}{% endif %}
          {% endif %}
        {% endif %}
      {% endif %}
    {% endif %}
  {% endif %}
{%- endmacro %}

{# numeric picker (returns float) #}
{% macro pick_float(r, a, b, c, d, e, f) -%}
  {% set v = r.get(a) %}
  {% if v is not none and (v|string|trim) != "" %}{{ v|float }}{% else %}
    {% set v = r.get(b) %}
    {% if v is not none and (v|string|trim) != "" %}{{ v|float }}{% else %}
      {% set v = r.get(c) %}
      {% if v is not none and (v|string|trim) != "" %}{{ v|float }}{% else %}
        {% set v = r.get(d) %}
        {% if v is not none and (v|string|trim) != "" %}{{ v|float }}{% else %}
          {% set v = r.get(e) %}
          {% if v is not none and (v|string|trim) != "" %}{{ v|float }}{% else %}
            {% set v = r.get(f) %}
            {% if v is not none and (v|string|trim) != "" %}{{ v|float }}{% else %}{{ 0.0 }}{% endif %}
          {% endif %}
        {% endif %}
      {% endif %}
    {% endif %}
  {% endif %}
{%- endmacro %}

{# =========================================================
   1) Source entries (child table)
   ========================================================= #}
{% set entries = doc.hourly_entries or [] %}

{# Last hour (for display only) #}
{% set last_hour = namespace(v="") %}
{% for r in entries %}
  {% set h = (pick(r, "hourly_slot", "hour_slot", "hour", "hours", "time_slot", "slot")|string|trim) %}
  {% if h != "" %}
    {% set last_hour.v = h %}
  {% endif %}
{% endfor %}

{# =========================================================
   2) Unique drills across ALL entries
   ========================================================= #}
{% set drills_list = [] %}
{% for r in entries %}
  {% set d = (pick(r, "drills", "drill", "drill_no", "rig", "machine", "equipment")|string|trim) %}
  {% if d != "" and (d not in drills_list) %}
    {% set _ = drills_list.append(d) %}
  {% endif %}
{% endfor %}

{# =========================================================
   3) Build drill rows ONCE + material totals from SAME drill rows
   ========================================================= #}
{% set drill_rows = [] %}
{% set mat_totals = {} %}
{% set shift_total_m = namespace(v=0.0) %}

{% if drills_list and (drills_list|length > 0) %}
  {% for d in drills_list %}
    {% set agg = namespace(total=0.0, area="", material="") %}

    {% for r in entries %}
      {% set rd = (pick(r, "drills", "drill", "drill_no", "rig", "machine", "equipment")|string|trim) %}
      {% if rd == d %}
        {# meters fieldnames (common variants) #}
        {% set m = pick_float(r, "meters", "meter", "metres", "total_meters", "drilled_meters", "drilling_meters") %}
        {% set agg.total = agg.total + (m|float) %}

        {# area fieldnames (common variants) #}
        {% if (agg.area|trim == "") %}
          {% set a = (pick(r, "mining_area", "area", "pit", "section", "location", "working_area")|string|trim) %}
          {% if a != "" %}{% set agg.area = a %}{% endif %}
        {% endif %}

        {# material fieldnames (common variants) #}
        {% if (agg.material|trim == "") %}
          {% set mt = (pick(r, "material", "material_type", "ore_type", "rock_type", "ground_type", "mat")|string|trim) %}
          {% if mt != "" %}{% set agg.material = mt %}{% endif %}
        {% endif %}
      {% endif %}
    {% endfor %}

    {% set row_material = (agg.material or "-") %}
    {% set _ = drill_rows.append({
      "drill": d,
      "area": agg.area or "-",
      "material": row_material,
      "meters": agg.total
    }) %}

    {% set shift_total_m.v = shift_total_m.v + agg.total %}

    {% if not mat_totals.get(row_material) %}
      {% set _ = mat_totals.update({row_material: 0.0}) %}
    {% endif %}
    {% set _ = mat_totals.update({row_material: mat_totals[row_material] + agg.total}) %}
  {% endfor %}
{% endif %}

{# =========================================================
   4) Monthly Stats (SAFE QUERY)
   ✅ Forecasted meters from drilling_meters_forecast
   ========================================================= #}
{% set dp = None %}
{% if doc.drilling_meter_planning %}
  {% set meta = frappe.get_meta("Drilling Meter Planning") %}
  {% set dp_fields = [] %}

  {% if meta.has_field("monthly_target_meters") %}{% set _ = dp_fields.append("monthly_target_meters") %}{% endif %}
  {% if meta.has_field("daily_target") %}{% set _ = dp_fields.append("daily_target") %}{% endif %}
  {% if meta.has_field("meter_per_drill") %}{% set _ = dp_fields.append("meter_per_drill") %}{% endif %}
  {% if meta.has_field("mtd_drills_meter") %}{% set _ = dp_fields.append("mtd_drills_meter") %}{% endif %}
  {% if meta.has_field("remaining_meter") %}{% set _ = dp_fields.append("remaining_meter") %}{% endif %}
  {% if meta.has_field("current_rate") %}{% set _ = dp_fields.append("current_rate") %}{% endif %}
  {% if meta.has_field("required_hourly_rate") %}{% set _ = dp_fields.append("required_hourly_rate") %}{% endif %}
  {% if meta.has_field("drilling_meters_forecast") %}{% set _ = dp_fields.append("drilling_meters_forecast") %}{% endif %}

  {% if dp_fields and (dp_fields|length > 0) %}
    {% set dp = frappe.db.get_value(
      "Drilling Meter Planning",
      doc.drilling_meter_planning,
      dp_fields,
      as_dict=1
    ) %}
  {% endif %}
{% endif %}

<div class="title">Drilling Shift Summary</div>

<div class="meta">
  <b>Site:</b> {{ doc.site or "-" }}
  &nbsp;|&nbsp;
  <b>Shift:</b> <span class="pill">{{ doc.shift or "-" }}</span>
  &nbsp;|&nbsp;
  <b>Date:</b> {{ doc.date or "-" }}
  &nbsp;|&nbsp;
  <b>Last Hour:</b> {{ last_hour.v or "-" }}
</div>

<table class="layout">
<tr>

  <td class="left">

    <div class="block">
      <div class="block-title">Drill Production (Full Shift: First Hour → Last Hour)</div>
      <table class="tbl" id="drill-production-table">
        <thead>
          <tr>
            <th style="width: 64px;">Drills</th>
            <th>Mining<br>Area</th>
            <th>Material</th>
            <th style="width: 56px;">Meters</th>
            <th style="width: 62px;">Start<br>Hours</th>
            <th style="width: 62px;">End<br>Hours</th>
            <th style="width: 52px;">Total<br>Hours</th>
          </tr>
        </thead>
        <tbody>

          {% if drill_rows and (drill_rows|length > 0) %}
            {% for row in drill_rows %}
              <tr>
                <td>{{ row.drill }}</td>
                <td>{{ row.area }}</td>
                <td>{{ row.material }}</td>
                <td class="right-align">{{ fmt1_or_dash(row.meters) }}</td>

                <td class="hours-cell start">
                  <input type="number" step="1" class="start-hours" placeholder="0">
                </td>
                <td class="hours-cell end">
                  <input type="number" step="1" class="end-hours" placeholder="0">
                </td>
                <td class="hours-cell total">
                  <input type="text" class="total-hours" placeholder="0" readonly>
                </td>
              </tr>
            {% endfor %}
          {% else %}
            <tr><td colspan="7">No Drill Data Found</td></tr>
          {% endif %}

        </tbody>
      </table>
    </div>

    <div class="block">
      <div class="block-title">Shift Report</div>
      <table class="tbl">
        <tbody>

          <tr>
            <td class="stats-label" colspan="2">DRILLED MATERIAL (Total Meters)</td>
          </tr>

          {% if mat_totals and (mat_totals|length > 0) %}
            {% for m, total in mat_totals.items() %}
              <tr>
                <td class="stats-label">{{ m }}</td>
                <td class="stats-value">{{ fmt1_or_dash(total) }}</td>
              </tr>
            {% endfor %}
          {% else %}
            <tr><td class="stats-label">-</td><td class="stats-value">-</td></tr>
          {% endif %}

          <tr>
            <td class="stats-label">Total Shift Meters</td>
            <td class="stats-value">{{ fmt1_or_dash(shift_total_m.v) }}</td>
          </tr>

        </tbody>
      </table>
    </div>

    {# DEBUG section unchanged #}
    {% if entries and (entries|length > 0) and (drills_list|length == 0) %}
      <div class="block">
        <div class="block-title">DEBUG: Hourly Entries Fieldnames (why table is empty)</div>
        <div class="debug">
          <div><b>Found hourly_entries rows:</b> {{ entries|length }}</div>
          <div><b>But could not find a drill field (drills/drill/drill_no/rig/machine/equipment).</b></div>
          <br>
          {% set r0 = entries[0] %}
          <div><b>First row keys:</b>
            {{ (r0.as_dict().keys()|list)|join(", ") }}
          </div>
          <div><b>First row sample:</b> {{ r0.as_dict() }}</div>
        </div>
      </div>
    {% endif %}

  </td>

  <td class="right">
    <div class="block">
      <div class="block-title">Monthly Statistics</div>
      <table class="tbl">
        <tbody>
          <tr><td class="stats-label">Monthly Target Meters</td><td class="stats-value">{{ fmt1_or_dash(dp.get("monthly_target_meters")) if dp and (dp.get("monthly_target_meters") is not none) else "-" }}</td></tr>
          <tr><td class="stats-label">Daily Target</td><td class="stats-value">{{ fmt1_or_dash(dp.get("daily_target")) if dp and (dp.get("daily_target") is not none) else "-" }}</td></tr>

          {% if dp and (dp.get("meter_per_drill") is not none) %}
            <tr><td class="stats-label">Meter per Drill</td><td class="stats-value">{{ fmt1_or_dash(dp.get("meter_per_drill")) }}</td></tr>
          {% endif %}

          <tr><td class="stats-label">MTD Drill Meters</td><td class="stats-value">{{ fmt1_or_dash(dp.get("mtd_drills_meter")) if dp and (dp.get("mtd_drills_meter") is not none) else "-" }}</td></tr>
          <tr><td class="stats-label">Remaining Meters</td><td class="stats-value">{{ fmt1_or_dash(dp.get("remaining_meter")) if dp and (dp.get("remaining_meter") is not none) else "-" }}</td></tr>
          <tr><td class="stats-label">Current Rate</td><td class="stats-value">{{ fmt1_or_dash(dp.get("current_rate")) if dp and (dp.get("current_rate") is not none) else "-" }}</td></tr>
          <tr><td class="stats-label">Required Rate</td><td class="stats-value">{{ fmt1_or_dash(dp.get("required_hourly_rate")) if dp and (dp.get("required_hourly_rate") is not none) else "-" }}</td></tr>

          <tr>
            <td class="stats-label">Forecasted Meters</td>
            <td class="stats-value">{{ fmt1_or_dash(dp.get("drilling_meters_forecast")) if dp and (dp.get("drilling_meters_forecast") is not none) else "-" }}</td>
          </tr>
        </tbody>
      </table>
    </div>
  </td>

</tr>
</table>

<script>
  function calcRowTotal(tr) {
    const startEl = tr.querySelector('input.start-hours');
    const endEl = tr.querySelector('input.end-hours');
    const totalEl = tr.querySelector('input.total-hours');
    if (!startEl || !endEl || !totalEl) return;

    const sRaw = startEl.value;
    const eRaw = endEl.value;

    if (sRaw === "" || eRaw === "") { totalEl.value = ""; return; }

    const s = parseInt(sRaw, 10);
    const e = parseInt(eRaw, 10);

    if (isNaN(s) || isNaN(e)) { totalEl.value = ""; return; }

    let t = e - s;

    // midnight rollover support:
    if (t < 0) t += 24;

    if (t < 0) t = 0;

    totalEl.value = String(t);
  }

  document.addEventListener('input', function(ev) {
    if (!ev.target) return;
    if (ev.target.classList.contains('start-hours') || ev.target.classList.contains('end-hours')) {
      const tr = ev.target.closest('tr');
      if (tr) calcRowTotal(tr);
    }
  }, true);

  setTimeout(function() {
    document.querySelectorAll('#drill-production-table tbody tr').forEach(calcRowTotal);
  }, 300);
</script>
