css:
  /* Layout */
  .header { text-align: center; margin-bottom: 10px; }
  .info p { margin: 2px 0; }
  .section { margin-bottom: 20px; }

  /* 2-column table styling */
  .table-2col, .subtable { width: 100%; border-collapse: collapse; margin-top: 10px; }
  .table-2col td, .subtable th, .subtable td { border: 1px solid #333; padding: 4px; }
  .table-2col td:first-child, .subtable td:first-child { font-weight: bold; width: 65%; }
  .table-2col td:last-child, .subtable td:last-child { text-align: right; }
  .subtable th { background-color: #f0f0f0; font-weight: bold; }

  /* Section Headings */
  .ts h3,
  .dozing h3,
  .grand-total h3 { margin-bottom: 8px; }

  /* Footer Notes */
  .footer-notes { font-style: italic; font-size: 0.9em; }
