css:
/* Center the report title */
.header h1 {
  text-align: center;
  font-weight: bold;
  font-size: 19pt;
  margin-bottom: 6px;
}

/* Document Info */
.info p {
  margin: 2px 0;
  font-size: 12pt;
}
.info {
  margin-bottom: 24pt;
}

/* Two-column layout */
.columns {
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
}
.column-left,
.column-right {
  width: 48%;
  vertical-align: top;
}

/* Base table styling */
.table-2col,
.subtable,
.mtd {
  width: 100%;
  border-collapse: collapse;
  margin-top: 8px;
}
.table-2col td,
.subtable td,
.mtd td {
  border: 1px solid #333;
  padding: 4px;
  font-size: 10pt;
}

/* Right-align every second column cell */
.table-2col td:nth-child(2),
.subtable td:nth-child(2),
.mtd td:nth-child(2) {
  text-align: right;
}

/* Remove any Excavator header row */
.subtable th {
  display: none;
}

/* Unified section heading styling */
.column-left h3,
.column-left h4,
.column-right h4 {
  font-size: 1.2em;
  font-weight: bold;
  background-color: #f0f0f0;
  padding: 4px;
  margin: 0 0 6px 0;
}

/* Space after each table in the left column */
.column-left table {
  margin-bottom: 12px;
}

/* Footer Notes */
.footer-notes {
  font-style: italic;
  font-size: 0.9em;
  margin-top: 20px;
}

html:
{# Clear unsaved flags to suppress warning #}
<script>
  if (window.cur_frm && cur_frm.doc) {
    cur_frm.doc.__unsaved = false;
    cur_frm.doc._unsaved = false;
    cur_frm.dirty = false;
    cur_frm.doc.__unsaved = 0;
    cur_frm.doc._unsaved = 0;
  }
  window.onbeforeunload = null;
</script>

{% set print_settings = {
  "page_size": "A4",
  "orientation": "Portrait",
  "margin_top": "5mm",
  "margin_bottom": "5mm",
  "margin_left": "5mm",
  "margin_right": "5mm"
} %}

<style>
  /* Global font reduction */
  body { font-size: 9pt; }

  .header { text-align: center; margin-bottom: 4mm; }
  .header h1 { font-size: 14pt; margin: 0; }

  .info { margin-bottom: 4mm; font-size: 9pt; }
  .info p { margin: 0.5mm 0; }

  /* Two-column layout with borders */
  .columns {
    display: flex;
    flex-wrap: nowrap;
    width: 100%;
  }
  .column-left,
  .column-right {
    width: 48%;
    padding: 0 1%;
    vertical-align: top;
    box-sizing: border-box;
    border: 1px solid #000;
  }

  .section { margin-bottom: 4mm; }

  table {
    width: 100%;
    border-collapse: collapse;
    font-size: 8.5pt;
  }
  table td {
    padding: 1px 3px;
    vertical-align: top;
  }
  /* Right-align every second column cell */
  table td:nth-child(2) {
    text-align: right;
  }
  h3, h4 {
    margin: 1mm 0;
    font-weight: bold;
  }
</style>

<!-- Report Header & Document Info -->
<div class="header">
  <h1>{{ doc.title or "Hourly Production Report" }}</h1>
</div>
<div class="info">
  <p><strong>Site:</strong> {{ doc.location }}</p>
  <p><strong>Production Shift Start Date:</strong> {{ doc.prod_date }}</p>
  <p><strong>Shift:</strong> {{ doc.shift }}</p>
  <p><strong>Hour Slot:</strong> {{ doc.hour_slot }}</p>
</div>

<div class="columns">
  <!-- Column 1 -->
  <div class="column-left">
    <div class="section">
      <h3>Hour Truck and Shoval BCMs</h3>
      <table>
        <tr><td>Total Softs BCMs</td><td>{{ "{:,.0f}".format(doc.total_softs_bcm|default(0)) }}</td></tr>
        <tr><td>Total Hards BCMs</td><td>{{ "{:,.0f}".format(doc.total_hards_bcm|default(0)) }}</td></tr>
        <tr><td>Total Coal BCMs</td><td>{{ "{:,.0f}".format(doc.total_coal_bcm|default(0)) }}</td></tr>
        <tr><td>Total Truck & Shoval BCMs</td><td>{{ "{:,.0f}".format(doc.total_ts_bcm|default(0)) }}</td></tr>
        <tr><td>Number of Production Trucks</td><td>{{ doc.num_prod_trucks|default(0) }}</td></tr>
        <tr><td>Average BCM per Truck</td><td>{{ "{:,.1f}".format(doc.ave_bcm_prod_truck|default(0)) }}</td></tr>
      </table>
    </div>

    <div class="section">
      <h4>BCMs by Excavator</h4>
      {% if doc.total_ts_bcm|default(0) == 0 %}
        <p>No excavator activity</p>
      {% else %}
        <table>
          {% set ns = namespace(shovels={}) %}
          {% for load in doc.truck_loads|default([]) %}
            {% if load.asset_name_shoval %}
              {% set curr = ns.shovels.get(load.asset_name_shoval, 0) %}
              {% set _ = ns.shovels.update({ load.asset_name_shoval: curr + (load.bcms|default(0)) }) %}
            {% endif %}
          {% endfor %}
          {% for name, total in ns.shovels.items() %}
            <tr><td>{{ name }}</td><td>{{ "{:,.0f}".format(total) }}</td></tr>
          {% endfor %}
        </table>
      {% endif %}
    </div>

    <div class="section">
      <h3>Hour Dozing BCMs</h3>
      <table>
        <tr><td>Total Dozing BCMs</td><td>{{ "{:,.0f}".format(doc.total_dozing_bcm|default(0)) }}</td></tr>
        <tr><td>Number of Production Dozers</td><td>{{ doc.num_prod_dozers|default(0) }}</td></tr>
        <tr><td>Average BCM per Prod Dozer</td><td>{{ "{:,.1f}".format(doc.ave_bcm_dozer|default(0)) }}</td></tr>
      </table>
    </div>

    <div class="section">
      <h3>Grand Total</h3>
      <table>
        <tr><td>Hour Total BCMs</td><td>{{ "{:,.0f}".format(doc.hour_total_bcm|default(0)) }}</td></tr>
        <tr><td>Truck & Shoval %</td><td>{{ "{:,.0f}".format(doc.ts_percent|default(0)) }}%</td></tr>
        <tr><td>Dozing %</td><td>{{ "{:,.0f}".format(doc.dozing_percent|default(0)) }}%</td></tr>
      </table>
    </div>
  </div>

  <!-- Column 2 -->
  <div class="column-right">
    <div class="section">
      <h4>MtD Summary</h4>
      <table>
        <tr><td>Month Target BCM</td><td>{{ "{:,.0f}".format(doc.monthly_target_bcm|default(0)) }}</td></tr>
        <tr><td>Target BCM per Day</td><td>{{ "{:,.0f}".format(doc.target_bcm_day|default(0)) }}</td></tr>
        <tr><td>Target BCM per Hour</td><td>{{ "{:,.0f}".format(doc.target_bcm_hour|default(0)) }}</td></tr>
        <tr><td>MtD Actual TS Tallies</td><td>{{ "{:,.0f}".format(doc.month_act_ts_bcm_tallies|default(0)) }}</td></tr>
        <tr><td>MtD Actual Dozing Tallies</td><td>{{ "{:,.0f}".format(doc.month_act_dozing_bcm_tallies|default(0)) }}</td></tr>
        <tr><td>MtD Survey Variance Total</td><td>{{ "{:,.0f}".format(doc.monthly_act_tally_survey_variance|default(0)) }}</td></tr>
        <tr><td>MtD Actual BCM</td><td>{{ "{:,.0f}".format(doc.month_actual_bcm|default(0)) }}</td></tr>
        <tr><td>MtD BCM per Day</td><td>{{ "{:,.0f}".format(doc.mtd_bcm_day|default(0)) }}</td></tr>
        <tr><td>MtD BCM per Hour</td><td>{{ "{:,.0f}".format(doc.mtd_bcm_hour|default(0)) }}</td></tr>
        <tr><td>MtD Forecasted BCM</td><td>{{ "{:,.0f}".format(doc.month_forecated_bcm|default(0)) }}</td></tr>
      </table>
    </div>
  </div>
</div>

<div class="footer-notes">
  <p><em>{{ doc.footer_notes or "Footer notes or disclaimers." }}</em></p>
</div>
