css:
/* Center the report title */
.header h1 {
  text-align: center;
  font-weight: bold;
  font-size: 19pt;
  margin-bottom: 6px;
}

/* Document Info */
.info p {
  margin: 2px 0;
  font-size: 12pt;
}
.info {
  margin-bottom: 24pt;
}

/* Two-column layout */
.columns {
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
}
.column-left,
.column-right {
  width: 48%;
  vertical-align: top;
}

/* Base table styling */
.table-2col,
.subtable,
.mtd {
  width: 100%;
  border-collapse: collapse;
  margin-top: 8px;
}
.table-2col td,
.subtable td,
.mtd td {
  border: 1px solid #333;
  padding: 4px;
  font-size: 10pt;
}

/* Right-align every second column cell */
.table-2col td:nth-child(2),
.subtable td:nth-child(2),
.mtd td:nth-child(2) {
  text-align: right;
}

/* Remove any Excavator header row */
.subtable th {
  display: none;
}

/* Unified section heading styling */
.column-left h3,
.column-left h4,
.column-right h4 {
  font-size: 1.2em;
  font-weight: bold;
  background-color: #f0f0f0;
  padding: 4px;
  margin: 0 0 6px 0;
}

/* Space after each table in the left column */
.column-left table {
  margin-bottom: 12px;
}

/* Footer Notes */
.footer-notes {
  font-style: italic;
  font-size: 0.9em;
  margin-top: 20px;
}
